    func pickerView(pickerView: UIPickerView, numberOfRowsInComponent component: Int) -> Int {
        
        return languages38.count
    
    }
    
    func pickerView(pickerView: UIPickerView, titleForRow row: Int, forComponent component: Int) -> String? {
        let rowTitle = "\(languages38[row].1) \(languages38[row].4)"
        return rowTitle
    }
    
    func pickerView(pickerView: UIPickerView, didSelectRow row: Int, inComponent component: Int) {
       currentLanguage = languages38[row]
    }
    
    //UITableView Function
    func tableView(tableView: UITableView, numberOfRowsInSection section: Int) -> Int {
        return items.count
    }
    
    func tableView(tableView: UITableView, cellForRowAtIndexPath indexPath: NSIndexPath) -> UITableViewCell {
        let cell = tableView.dequeueReusableCellWithIdentifier("myCell")!
        
            cell.textLabel?.text=items[indexPath.row]
            //cell.detailTextLabel?.text="subtitles"
        
             return cell
    }
    
    func tableView(tableView: UITableView, didSelectRowAtIndexPath indexPath: NSIndexPath) {
    
        speakThisString(items[indexPath.row])
    
    }
    
    func speakThisString(passedString: String){
        let myUtterance = AVSpeechUtterance(string: passedString)
        
        myUtterance.voice = AVSpeechSynthesisVoice(language: currentLanguage.0)
        myUtterance.rate = rateFloat
        
        mySpeechSynth.speakUtterance(myUtterance)
    }

